import Foundationstruct JobResponse: Decodable {    let jobCount: Int    let jobs: [Job]        enum CodingKeys: String, CodingKey {        case jobCount = "job-count"        case jobs    }}struct Job: Decodable {    let id: Int    let url: String    let title: String    let companyName: String?    let companyLogo: String?    let category: String    let jobType: String?    let publicationDate: String    let candidateRequiredLocation: String    let salary: String?    let description: String?        enum CodingKeys: String, CodingKey {        case id        case url        case title        case companyName = "company_name"        case companyLogo = "company_logo"        case category        case jobType = "job_type"        case publicationDate = "publication_date"        case candidateRequiredLocation = "candidate_required_location"        case salary        case description    }}