////  LoginViewController.swift//  CareerMentor//import UIKitimport FirebaseAuthimport FirebaseFirestoreclass LoginViewController: UIViewController {    @IBOutlet weak var usernameField: UITextField!    @IBOutlet weak var passwordField: UITextField!    let db = Firestore.firestore()    override func viewDidLoad() {        super.viewDidLoad()        passwordField.isSecureTextEntry = true    }    @IBAction func loginTapped(_ sender: UIButton) {        guard let username = usernameField.text, !username.isEmpty,              let password = passwordField.text, !password.isEmpty else {            showAlert(message: "Please enter both username and password.")            return        }        db.collection("users").whereField("username", isEqualTo: username).getDocuments { (snapshot, error) in            if let error = error {                self.showAlert(message: "Error: \(error.localizedDescription)")                return            }            guard let documents = snapshot?.documents, !documents.isEmpty else {                self.showAlert(message: "Username not found.")                return            }            let userData = documents.first!.data()            guard let email = userData["email"] as? String else {                self.showAlert(message: "Invalid user data.")                return            }            Auth.auth().signIn(withEmail: email, password: password) { (result, error) in                if let error = error {                    self.showAlert(message: "Login failed: \(error.localizedDescription)")                    return                }                // Show success alert and stay on login screen                self.showAlert(message: "Login successful. Welcome, \(username)!")            }        }    }    func showAlert(message: String) {        let alert = UIAlertController(title: "Login Info", message: message, preferredStyle: .alert)        alert.addAction(UIAlertAction(title: "OK", style: .default))        present(alert, animated: true)    }}